import "./css/ProductCard.css"
import img from "../img/img.png";

class ProductCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
        };
    }
    render() { 
        console.log(this.props.paragraphs);
        return ( 
            <div className="product-card">
                <div className="card-img">
                    <img src={img} alt="img"/>
                </div>
                <div className="card-content">
                    <h1 className="main-header">{this.props.main_header}</h1>
                    <h2 className='second-header'>{this.props.second_header}</h2>
                    <div className="content-text">
                        {this.props.paragraphs.map((p, id) => (
                            <p key={id}>{p}</p>
                        ))}
                    </div>
                    <button className="more-btn">VEÄŒ O IZDELKU {this.props.id + 1}</button>
                </div>
            </div>
         );
    }
}

export default ProductCard;
