        </div>
        <div className="card-content">
            <h1 className="main-header">{props.main_header}</h1>
            <h2 className='second-header'>{props.second_header}</h2>
            <div className="content-text">{props.text.substring(0, 300) + "..."}</div>
            <button className="more-btn">VEÄŒ O IZDELKU {props.id + 1}</button>
        </div>
    </div>
    )
} 

export default ProductCard;
